#!/usr/bin/env node

const minimist = require('minimist')
const opts = minimist(process.argv.slice(2))

let [command, ...args] = opts._

if (!command) {
  console.log(`\n\n    Usage: adalo COMMAND

    COMMAND:

      login
      build
      dev
      publish

  `)

  process.exit(1)
}

const { execute } = require('./index')

execute(command, args, opts)
